import React, { useState, useEffect } from 'react';
import './App.css';

const gridSize = 6;

function App() {
  const [tiles, setTiles] = useState([]);
  const [selectedTiles, setSelectedTiles] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState(0);
  const [lockBoard, setLockBoard] = useState(false);

  useEffect(() => {
    initializeGame();
  }, []);

  const initializeGame = () => {
    const numbers = Array.from({ length: gridSize * gridSize / 2 }, (_, index) => index + 1);
    
    const shuffledNumbers = [...numbers, ...numbers].sort(() => Math.random() - 0.5);
     
    setTiles(shuffledNumbers.map((number, index) => ({ id: index, number, flipped: false, matched: false })));
    console.log(tiles)
    setMatchedPairs(0);
  };

  const handleTileClick = (tile) => {
    if (lockBoard || tile.flipped || selectedTiles.length === 2) return;

    const updatedTiles = tiles.map((t) => (t.id === tile.id ? { ...t, flipped: true } : t));
    setTiles(updatedTiles);
    setSelectedTiles([...selectedTiles, tile]);

    if (selectedTiles.length === 1) {
      if (selectedTiles[0].number === tile.number) {
        setMatchedPairs(matchedPairs + 1);
        setSelectedTiles([]);
      } else {
        setLockBoard(true);
        setTimeout(() => {
          flipBack(selectedTiles[0], tile);
        }, 1000);
      }
    }
  };

  const flipBack = (tile1, tile2) => {
    const updatedTiles = tiles.map((tile) =>
      tile.id === tile1.id || tile.id === tile2.id ? { ...tile, flipped: false } : tile
    );

    setTiles(updatedTiles);
    setSelectedTiles([]);
    setLockBoard(false);
  };

  useEffect(() => {
    if (matchedPairs === gridSize * gridSize / 2) {
      setTimeout(() => {
        initializeGame();
      }, 1000);
    }
  }, [matchedPairs]);

  return (
    <div className="App">
      <div className="game-grid">
        {tiles.map((tile) => (
          <div
            key={tile.id}
            className={`tile ${tile.flipped ? 'flipped' : ''} ${tile.matched ? 'matched' : ''}`}
            onClick={() => handleTileClick(tile)}
          >
            {tile.flipped && !tile.matched ? tile.number : ''}
          </div>
        ))}
      </div>
      {matchedPairs === gridSize * gridSize / 2 && <div className="message">Congratulations! You won!</div>}
    </div>
  );
}

export default App;
