import React,{useState} from "react";
import "./styles.css";
import Box from './components/Box'
export default function App() {
  const [grid,setGrid]=useState(Array.from({length:6},()=> new Array(6).fill(0)))

  const randomNumber=(i,j)=>{
    const newGrid=[...grid];
    newGrid[i][j]=Math.floor(Math.random()*18+1);
    setGrid(newGrid)

  }
  const elements=grid.map((row,i)=>(
    row.map((_,j)=>(
      <Box 
       key={`${i}-${j}`}
       onClick={()=>randomNumber(i,j)}
       number={grid[i][j]}
       /> 
    ))
  ))

  return (
    <div className="App">

    <div className='grid-container'>
      {elements}
    </div>



    </div>
  );
}
